        {path: 'numberInStock', label: 'Stock'},
        {path: 'dailyRentalRate', label: 'Rate'},
        {key: "delete", 
            content: movie => (
                <button 
                    onClick={() => this.props.onDelete(movie)} 
                    className="btn btn-danger btn-sm"
                >Delete</button>)}
    ]

    render() {
        const {movies, onDelete, sortColumn, onSort} = this.props;

