import React from 'react';
import Joi from 'joi-browser';
import Form from './common/form';

class MovieForm extends Form {
    state = {
        data: {title: '', genre_id: '', stock: '', rate: ''},
        errors: {}
    }

    schema = {
        title: Joi.string().required().label('Title'),
        genre_id: Joi.string().required().label('Genre'),
        stock: Joi.number().min(0).max(100).label('Number in Stock'),
        rate: Joi.number().min(0).max(10).label('Rate')
    }

    render() { 
        return ( 
            <div>
                <h1>MovieForm</h1>
                <form onSubmit={this.handleSubmit}>
                    {this.renderInput('title', 'Title')}
                    {this.renderSelect('genre_id', 'Genre')}
                    {this.renderInput('stock', 'Number in Stock')}
                    {this.renderInput('rate', 'Rate')}
                    {this.renderButton('Save')}
                </form>
            </div>
        );
    }
}

export default MovieForm;
